var visible=(input[0].length-1)*4;          //edges

for (var y=1;y<input.length-1;y++) {        //only checking within edges
  for (var x=1;x<input[y].length-1;x++) {   //maybe not square
    //console.log(input[y].charAt(x));
      if (seen(x,y)) { console.log('v');visible++; }
  }
}

function seen(startX,startY) {
  //takes coordinate
  //returns true if tree is seen from edge in any direction
  var tree = Number(input[startY].charAt(startX));
  console.log('checking for',startX,startY,tree);

  console.log('checking from up');
  var max = 0;
  for (var y=0;y<startY;y++) {
    if (input[y].charAt(startX) > max) { max = input[y].charAt(startX);}
  }
  if (max < tree) { return true; }

  console.log('checking from down');
  var max = 0;
  for (var y=startY+1;y<input.length;y++) {
    if (input[y].charAt(startX) > max) { max = input[y].charAt(startX);}
  }
  if (max < tree) { return true; }

  console.log('checking from left');
  max = 0;
  for (var x=0;x<startX;x++) {
    if (input[startY].charAt(x) > max) { max = input[startY].charAt(x);}
  }
  if (max < tree) { return true; }

  console.log('checking from right');
  max = 0;
  for (var x=startX+1;x<input[startY].length;x++) {
    if (input[startY].charAt(x) > max) { max = input[startY].charAt(x);}
  }
  if (max < tree) { return true; }

  return false;
}

console.log("First part:",visible);

//console.log("Second part:",visible);


function readInput(filename) {
  const fs = require('fs');

  try {
    const data = fs.readFileSync(filename, 'utf8');
    return data.split(/\r?\n/);
  } catch (err) {
    console.error(err);
  }
}